import React from "react";
import "./Shop.scss";
import { Link } from "react-router-dom";

function Shop() {
  return (
    <div className="Shop_App">
      <div className="Shop_one">
        <div className="Shop_Title">
          <h1 className="Shop_h1">
            Welcome to the shop<br></br> 
          </h1>
          <div className="Shop_explain">
            Draw the items you want to draw & become strong on the battlefield. <br></br>
            $ Event $  Class A Chance Increase!
          </div>

          <div className="Shop_App">
            <div className="Shop_Img">
            <img src="./img/집행검.jpg" alt="뽑기" />
            </div>

            <div className="Shop_form"></div>
          </div>




          {/* <Link to="/MyPage"> */}
            <button className="Shop_title_button"> 토큰 충전</button>

            <div>
            {account ? (
              <div>
                <p>나의 계정주소: {account}</p>
                <p>My Erc20 Token Address: {erc20TokenAddr}</p>
                <div>
                  <input
                    type="text"
                    value={inputValue}
                    onChange={(e) => {
                      setInputValue(e.target.value);
                    }}
                  />
                  <button
                    className="MyPage_title_button2"
                    onClick={generateToken}
                  >
                    {" "}
                    내가 보낼 '아이템의 주소'
                  </button>
                </div>
                <div className="MyPage_title_text">
                  <input
                    type="text"
                    value={sendToAddr}
                    onChange={(e) => {
                      setSendToAddr(e.target.value);
                    }}
                  />

                  <button
                    className="MyPage_title_button3"
                    onClick={sendErc20Token}
                  >
                    상대방이 아이템을 '받을 주소'
                  </button>
                </div>
              </div>
            ) : (
              <button className="MyPage_title_button" onClick={connectWallet}>
                Connect With My Wallet
              </button>
            )}
          </div>

          {/* </Link> */}



        </div>
        <div className="Shop_lists">

          <div className="Shop_list">Item draw</div>

          <div className="Shop_list">Item probability table</div>

        </div>
      </div>


    </div>
  );
}

export default Shop;
